sys_script_include.script:
var WeatherAPIIntegration = Class.create();
WeatherAPIIntegration.prototype = {
    initialize: function() {},
    getWeatherData: function(city) {
        var apiKey = '565340fab01d4e2499f54819252502'; // Replace with your actual API key
		// http://api.weatherapi.com/v1/current.json?q=london&key=565340fab01d4e2499f54819252502
        var endpoint = 'http://api.weatherapi.com/v1/current.json?q='+city+'&key='+apiKey;
        var request = new sn_ws.RESTMessageV2();
        request.setEndpoint(endpoint);
        request.setHttpMethod('GET');

        try {
            var response = request.execute();
            var responseBody = response.getBody();
            var responseObj = JSON.parse(responseBody);

            if (response.getStatusCode() != 200) {
                gs.error('Error fetching weather data: ' + response.getErrorMessage());
                return null;
            }

            return responseObj;
        } catch (ex) {
            gs.error('Exception: ' + ex.message);
            return null;
        }
    },


    type: 'WeatherAPIIntegration'
};
